#pragma config(UserModel, "/setup-functions/ports_setup.h")
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

bool startedClose = false; // Variable to store which side the robot starts on, true if close, false if far

// Importing files
#include "setup-functions/setup.c"							// Contains all the setup code
#include "setup-functions/misc_functions.c" 				// Contains all the miscellaneous functions
#include "setup-functions/sensing_functions.c" 				// Contains all the functions for sensing
#include "setup-functions/new_movement_functions.c" 		// Contains all the base movement functions
#include "setup-functions/simple_movement_functions.c"		// COntains all the simple movement functions

#include "setup-functions/tasks.c"
#include "code.c"

//CODE:

task main()
{
	// Any other setup code is done here
	resetMotors();									// Resets motors
	clearTimer(T1);									// Clears the timer for timing the run

	// Runs all the zones here


	// while (1){
	// 	displayCenteredBigTextLine(5, "G:%d, B:%d, %d", getGreen(4), getBlue(4), (getGreen(4)>=getBlue(4) || getBlue(4)<getGreen(4)+2));
	// }

	displayCenteredBigTextLine(8, "Battery: %d%%", 100.0*(getBatteryVoltage()-minVoltage)/(maxVoltage-minVoltage));

	if (startedClose)
	{
		closeCode();
	}
	else
	{
		farCode();
	}

	moveArmAbs(50, defaultArmMinspeed, defaultArmMinspeed, 220, defaultAcc, defaultAcc, LIFT);
	moveArmAbs(50, defaultArmMinspeed, defaultArmMinspeed, -104, defaultAcc, defaultAcc, CLAW);
	moveNoSyncParam(100, 100, defaultMinspeed, defaultMinspeed, 594, defaultAcc, defaultAcc, true, RELDEG);
	turn2MotorAccMinspeed(20, defaultMinspeed, defaultMinspeed, 115, defaultAcc, defaultAcc, true);
	moveNoSyncParam(-100, -100, defaultMinspeed, defaultMinspeed, 1089, defaultAcc, defaultAcc, false, RELDEG);
	moveSimpleWallSquare(-100, -100, 1000, TIME);
	setSpeed(0, 0);
	resetRelative();
	turn1MotorAccMinspeed(100, defaultMinspeed, defaultMinspeed, 90, defaultAcc, defaultAcc, true, RIGHT);
	moveSenseOneSensorSimple(-50, -50, defaultMinspeed, true, 1, BLACK, COLOR);
	turn2MotorAccMinspeed(40, defaultMinspeed, defaultMinspeed, 93, defaultAcc, defaultAcc, true);
	moveSimpleWallSquare(-100, -100, 1000, TIME);
	setSpeed(0, 0);
	resetRelative();
	moveNoSyncParam(100, 100, defaultMinspeed, defaultMinspeed, 587, 0.2, 0.2, true, RELDEG);
	turn1MotorAccMinspeed(40, defaultMinspeed, defaultMinspeed, 92, defaultAcc, defaultAcc, true, LEFT);
	moveSenseOneSensorSimple(20, 20, defaultMinspeed, true, 3, WHITE, COLOR);
	setArmSpeed(LIFT, -100);
	sleep(300);

	// Time display after the challenge is finished
	displayCenteredBigTextLine(5, "Time: %0.2f", (time100(T1)/10.0));

	// Sound after the challenge is finished
	if (time100(T1)>1200)
	{
		playSound(soundDownwardTones);				// Play negative sound if the challenge took too long
	}
	else
	{
		playSound(soundUpwardTones);				// PLay positive sound if the challenge was in time
	}

	sleep(10000);

	stopAllTasks();
}
