// Importing files
#include "setup.c"									// Contains all the setup code
#include "misc_functions.c" 						// Contains all the miscellaneous functions
#include "sensing_functions.c" 						// Contains all the functions for sensing
#include "movement_functions.c" 					// Contains all the movement functions

#include "zone_close_start.c"						// Stores the function loop1CloseStart which is the code for the red, yellow, debris, and water pipes
#include "zone_far_start.c"							// Same as the line above but for the far starting position
#include "second_loop.c"							// Stores the function loop2 which is the code for the blue and green

//CODE:

bool startedClose = true; // Variable to store which side the robot starts on, true if close, false if far

task main()
{
	clearDebugStream();

	// Any other setup code is done here
	resetMotors();									// Resets motors
	clearTimer(T1);									// Clears the timer for timing the run
	//startTask(odometry);							// Starts the odometry loop
	//startTask(music);								// Starts the music playing

	// Runs all the zones here

	loop1CloseStart(); // If the side to start on is the close side, uncomment this and comment te next two lines

	// If the side to start on is the far side, uncomment the next two lines and communt the previous line
	// startedClose = false;
	// loop1FarStart();

	loop2(); // This is the function to do the blue and green blocks


	// Time display after the challenge is finished
	displayCenteredBigTextLine(5, "%0.2f", (time100(T1)/10.0));

	// Sound after the challenge is finished
	if (time100(T1)>1200)
	{
		playSound(soundDownwardTones);				// Play negative sound if the challenge took too long
	}
	else
	{
		playSound(soundUpwardTones);				// PLay positive sound if the challenge was in time
	}

	stopAllTasks(); 								// Stops all the tasks (Music and odometry)

	sleep(10000);

}
